import React, { Component } from 'react'
import { MDBInput } from 'mdbreact'
import axios from 'axios';

import ButtonCard from './ButtonCard'

export default class ButtonList extends Component {

    state= {
        url: "http://localhost:3000/users",
        button: null,
        search: '',
        desSearch: ''
    }

    onchange = e => {
        this.setState({search: e.target.value})
        
    }

    onchangeDes = e => {
        this.setState({desSearch: e.target.value})
    }

    renderButton= button => {
        if (this.state.button.length <= 0) {
            return <div>Loading..</div>
        }

        const {search, desSearch} = this.state

        if(search !== "" && button.buttonTag.toLowerCase().indexOf(search.toLowerCase()) === -1){
            return null;
        }

        // if(desSearch !== "" && button.description.toLowerCase().indexOf(desSearch.toLowerCase()) === -1){
        //     return null;
        // }

        return <ButtonCard
            key={button.name}
            name={button.name}
            url={button.url} 
            description={button.description}
            buttonTag = {button.buttonTag}
        />
    }

    async componentDidMount() {
        const res = await axios.get(this.state.url);
        this.setState({button: res.data['data']});
    }

    render() {
        return (
            <React.Fragment>
                <br></br>
                <br></br>
                <br></br>
                <div className='row' >
                    <div className='col'>
                        <MDBInput icon="search" label='Search' onChange={this.onchange}/>
                    </div>
                    <div className='col'>
                        <MDBInput icon="search" label='Description' onChange={this.onchangeDes} />
                    </div>
                </div>

                {this.state.button ? (
                    <div className='row'>
                        {this.state.button.map(button => {
                            return this.renderButton(button)
                        })}
                    </div>
                ) : (
                    <h1>Loading Buttons</h1>
                    )}
            </React.Fragment>
        )
    }
}
