import React, { Component } from 'react'

import { onOptionsChange } from './MultiSelect'


export class Option extends Component {

    constructor(props){
        super(props)
        this.state = { selected: false}
        this.handleChange = this.hanfleChange.bind(this)
    }

    handleChange(){
        this.setState(prevState => ({selected: !prevState.selected}),
        () => this.props.onOptionChange(this.props.option, this.state.selected))
    }
    render() {
        return (
            <div>
            <input
              type="checkbox"
              id={this.props.option}
              name="option name"
              value={this.props.option}
              onChange={this.handleChange}
              checked={this.state.selected}
            />
            <label htmlFor={this.props.option}>{this.props.option}</label>
          </div>
        )
    }
}

export default Option
